import React from "react";
import Link from "next/link";
import Head from "next/head";

import ApolloClient from "apollo-boost";
import { ApolloProvider, useQuery } from "@apollo/react-hooks";
import { InMemoryCache } from "apollo-cache-inmemory";

import { TopBar, Navigation } from "../../components/shared";

import { GET_COLLECTIONS } from "../../src/graphql/schema.graphql";
import GlobalStyles from "./index.styles";

interface IMainLayout {
  children: React.ReactElement;
}

const client = new ApolloClient({
  uri: process.env.REACT_APP_SHOPIFY_URL,
  cache: new InMemoryCache(),

  headers: {
    Accept: "application/json",
    "X-Shopify-Storefront-Access-Token": process.env.REACT_APP_SHOPIFY,
  },

  onError: (err) => console.log(err),
});

function MainLayout({ children }: IMainLayout) {
  const { data: collections, loading, error } = useQuery(GET_COLLECTIONS);

  console.log({ collections, loading, error });

  return (
    <>
      <Head>
        <link
          href="https://fonts.googleapis.com/css2?family=Nunito+Sans:wght@400;600;700&display=swap"
          rel="stylesheet"
        />
      </Head>
      <GlobalStyles />
      <ApolloProvider client={client}>
        <TopBar />
        <Navigation />
        <Link href="/about">
          <a>About</a>
        </Link>

        {children}
      </ApolloProvider>
    </>
  );
}

export default MainLayout;
