import React from "react";
import { useQuery } from "@apollo/react-hooks";

import { ICollectionsResponse } from "./types";
import { GET_COLLECTIONS } from "../src/graphql/schema.graphql";
import { Container } from "../src/styles";
import { Row, Col, Card } from "index.styles";
import Link from "next/link";

function Home() {
  const { data, loading } = useQuery<ICollectionsResponse>(GET_COLLECTIONS);

  if (loading) {
    return <p>Loading</p>;
  }

  return (
    <Container>
      <Row>
        {data.collections.edges.map(({ node: category }) => (
          <Col key={category.id}>
            {/* <img src={category.image.originalSrc} alt={category.title} /> */}
            <Link href="/categorie/[cid]" as={`/categorie/${category.handle}`}>
              <Card>
                <div className="image-wrap">
                  <div
                    className="image"
                    aria-label={category.title}
                    style={{
                      backgroundImage: `url(${category.image.originalSrc})`,
                    }}
                  ></div>
                </div>
                <h3 className="card-title">{category.title}</h3>
              </Card>
            </Link>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default Home;
