import React, { FC } from "react";
import Link from "next/link";

import { Card } from "./index.styles";

import ProductCardOverviewContent from "./components/ProductCardOverviewContent";
import ProductCardProductContent from "./components/ProductCardProductContent";

export enum ProductTypes {
  Overview = "overview",
  Product = "product",
}

interface IProductCard {
  title?: string;
  handle?: string;
  imageUrl?: string;
  type?: ProductTypes;
}

const ProductCard: FC<IProductCard> = ({
  title,
  handle,
  imageUrl,
  type = ProductTypes.Overview,
}) => {
  const LongArrow = "/images/icons/long-arrow.svg";
  let BottomComponent = null;

  switch (type) {
    case ProductTypes.Product:
      BottomComponent = (
        <ProductCardOverviewContent title={title} icon={LongArrow} />
      );
      break;
    case ProductTypes.Overview:
      BottomComponent = (
        <ProductCardOverviewContent title={title} icon={LongArrow} />
      );
      break;
  }

  return (
    <Card>
      <Link href="/categorie/[cid]" as={`/categorie/${handle}`}>
        <a>
          <div className="image-wrap">
            <div
              className="image"
              aria-label={title}
              style={{
                backgroundImage: `url(${imageUrl})`,
              }}
            ></div>
          </div>
          {/* {type === ProductTypes.Overview && (
            <ProductCardOverviewContent title={title} icon={LongArrow} />
          )}

          type */}

          <BottomComponent />
        </a>
      </Link>
    </Card>
  );
};

export default ProductCard;
