// TOASTER https://jossmac.github.io/react-toast-notifications/
import React, { FC, useState, useEffect } from "react";
import { useRouter } from "next/router";
import { useQuery, useMutation, useLazyQuery } from "@apollo/react-hooks";
import {
  GET_SINGLE_PRODUCT,
  CREATE_CHECKOUT,
} from "../../src/graphql/schema.graphql";

import { titlelize } from "../../src/helpers";
import { Container, padding } from "../../src/styles";
import { IProductByHandle, TVariant } from "./types";
import ProductGallery from "./components/ProductGallery/ProductGallery";
import { MainContent, Aside, Wrap } from "./Product.styles";
import ProductOptions from "./components/ProductGallery/components/ProductOptions";
import { formatCurrency } from "../../src/helpers/formatting";

const SingleProduct: FC = () => {
  const router = useRouter();
  const { pid } = router.query;
  const [variant, setVariant] = useState(null);
  const [checkoutId, setCheckoutId] = useState<string>();

  const { data, loading }: IProductByHandle = useQuery(GET_SINGLE_PRODUCT, {
    variables: {
      handle: pid,
    },
  });

  const [
    createCheckout,
    { loading: createCheckoutLoading, data: createCheckoutResponse },
  ] = useLazyQuery(CREATE_CHECKOUT, {
    variables: {
      skip: !data,
      lineItems: [
        {
          quantity: 1,
          variantId: variant.id,
        },
      ],
    },
  });

  const productTitle = titlelize(pid, true);

  useEffect(() => {
    if (!data) return;
    setVariant(data.productByHandle.variants.edges[0].node);
    setCheckoutId(localStorage.getItem("checkoutId"));
  }, [loading, data]);

  if (loading || !variant) {
    return null;
  }

  const product = data.productByHandle;
  const hasMultipleImages = product.images.edges.length > 1;
  const hasNoImage = !Boolean(product.images.edges.length);
  const hasProductOptions = Boolean(product.options);

  console.log({ createCheckoutLoading, createCheckoutResponse });

  const handleVariantChange = (id: string) => {
    const { node: variant } = product.variants.edges.find(
      ({ node }) => node.id === id
    );

    setVariant(variant);
  };

  const handleDirectBuy = () => {
    if (!checkoutId) {
      console.log("create checkout id");
      createCheckout();
    }
  };

  return (
    <>
      <Container>
        <div style={{ padding: `${padding.xxl} 0 ${padding.xl}` }}></div>
      </Container>

      <Wrap>
        <Container style={{ display: "flex" }}>
          <MainContent>
            <ProductGallery
              hasMultipleImages={hasMultipleImages}
              hasNoImage={hasNoImage}
              images={product.images.edges}
            />
          </MainContent>

          <Aside>
            {variant.currentlyNotInStock && (
              <span className="note warning margin-bottom-1">
                Tijdelijk niet beschikbaar
              </span>
            )}

            <h1>{productTitle}</h1>

            <p style={{ marginBottom: "1em", marginTop: "0" }}>
              {product.description}
            </p>

            <h2 style={{ marginBottom: "1em", color: "rgb(151, 149, 149)" }}>
              {formatCurrency(variant.priceV2.amount)}
            </h2>

            {hasProductOptions && (
              <ProductOptions
                variants={product.variants}
                onVariantChange={handleVariantChange}
              />
            )}

            <button type="button" onClick={handleDirectBuy}>
              Koop direct
            </button>
          </Aside>
        </Container>
      </Wrap>
    </>
  );
};

export async function getStaticPaths() {
  return {
    paths: ["/product/[pid]"],
    fallback: true,
  };
}

export async function getStaticProps(props: { params: { pid: string } }) {
  return {
    props: {
      pid: props.params.pid,
    },
  };
}

export default SingleProduct;
