import React, { FC } from "react";
import { useCart } from "../../src/context/cart";
import { REMOVE_ITEM } from "../../src/graphql/schema.graphql";
import Icon from "../../components/Icon/Icon";

import { Container, padding } from "../../styles";
import { useMutation } from "@apollo/react-hooks";

import { Wrap, Row } from "./Checkout.styles";

const Checkout: FC = () => {
  const { cartContent: parsedContent, setCartResponse } = useCart();
  // const parsedContent = Boolean(cartContent) && JSON.parse(cartContent);

  if (!parsedContent) {
    return (
      <Container>
        <div style={{ margin: `${padding.xxl} 0 ${padding.xl}` }}>
          <h1 className="section-title">
            <span>Uw winkelmandje</span>
          </h1>

          <p>U heeft nog niks in uw mandje.</p>
        </div>
      </Container>
    );
  }

  const { lineItems, id: checkoutId } = parsedContent;

  const [removeItem] = useMutation(REMOVE_ITEM);

  const handleDeleteClick = ({ id }) => {
    removeItem({
      variables: {
        checkoutId,
        lineItemIds: [id],
      },
    })
      .then((res) => res)
      .then(({ data }) => {
        setCartResponse(data.checkoutLineItemsRemove.checkout);
      });
  };

  return (
    <Container>
      <div style={{ margin: `${padding.xxl} 0 ${padding.xl}` }}>
        <h1 className="section-title">
          <span>Uw winkelmandje</span>
        </h1>

        <Wrap>
          {lineItems.edges.map(({ node }) => {
            console.log(node);
            return (
              <Row className="flex items-center" key={node.id}>
                <div className="thumbnail">
                  <div
                    style={{
                      backgroundImage: `url(${node.variant.image.originalSrc})`,
                    }}
                  ></div>
                </div>
                <div className="text-content flex">
                  <h3>{node.title}</h3>
                  <p>
                    {node.variant.selectedOptions[0].name}:{" "}
                    <strong>{node.variant.selectedOptions[0].value}</strong>
                  </p>
                </div>

                <div className="actions">
                  <button onClick={() => handleDeleteClick(node)}>
                    Verwijder
                    <Icon type="trash-can" fill="red" className="remove-icon" />
                  </button>
                </div>
              </Row>
            );
          })}
        </Wrap>
      </div>
    </Container>
  );
};

export default Checkout;
