import React, { FC } from "react";
import { useRouter } from "next/router";
import { titlelize } from "../../src/helpers";

const SingleProduct: FC = () => {
  const router = useRouter();
  const { pid } = router.query;

  const productTitle = titlelize(pid, true);

  return <p>hi i'm a {productTitle}</p>;
};

export async function getStaticPaths() {
  return {
    paths: [
      "/categorie/verlichting",
      "/categorie/banden",
      "/categorie/marathonwagens",
    ],
    fallback: true,
  };
}

export async function getStaticProps(props: { params: { cid: string } }) {
  return {
    props: {
      cid: props.params.cid,
    },
  };
}

export default SingleProduct;
