import React, { FC } from "react";
import { useRouter } from "next/router";
import { useQuery } from "@apollo/react-hooks";

import MainLayout from "../../layouts/main";
import { GET_SINGLE_COLLECTION } from "../../src/graphql/schema.graphql";

type TSingleProduct = {
  node: {
    id: string;
    title: string;
    handle: string;
  };
};

type TSingleCollection = {
  description?: string;
  id: string;
  products: {
    edges: Array<TSingleProduct>;
  };
};

interface ISingleCollection {
  data: {
    collectionByHandle: TSingleCollection;
  };
  loading: boolean;
}

const SingleCategory: FC = () => {
  const router = useRouter();
  const { cid } = router.query;

  const { data, loading }: ISingleCollection = useQuery(GET_SINGLE_COLLECTION, {
    variables: {
      handle: cid,
    },
  });

  return (
    <MainLayout>
      <>
        <p>I'm category: {cid}</p>

        {!loading && <p>done loading</p>}
      </>
    </MainLayout>
  );
};

export default SingleCategory;
