import React from "react";
import Head from "next/head";
import { useQuery } from "@apollo/react-hooks";

import { TopBar, Navigation } from "../../components/shared";

import { GET_COLLECTIONS } from "../../src/graphql/schema.graphql";
import GlobalStyles from "./index.styles";

interface IMainLayout {
  children: React.ReactElement;
}

function MainLayout({ children }: IMainLayout) {
  const {
    data: { collections: navItems },
    loading,
    error,
  } = useQuery(GET_COLLECTIONS);

  // console.log({ collections, loading, error });

  // console.log({ navItems });

  return (
    <>
      <Head>
        <link
          href="https://fonts.googleapis.com/css2?family=Nunito+Sans:wght@400;600;700&display=swap"
          rel="stylesheet"
        />
      </Head>
      <GlobalStyles />
      <TopBar />
      <Navigation isLoadingNavItems={loading} navItems={navItems} />

      {children}
    </>
  );
}

export default MainLayout;
