import styled from "styled-components";
import { rgba } from "polished";

export const Wrap = styled.div`
  box-shadow: 0px 2px 8px ${rgba("#000", 0.05)};
`;

export const Row = styled.div`
  border-radius: 4px;
  background-color: white;
  padding: 2em;

  .thumbnail {
    position: relative;
    width: 125px;
    height: 125px;

    > div {
      position: absolute;
      left: 0;
      top: 0;
      width: 100%;
      height: 100%;
      background-size: cover;
    }
  }
`;
