import styled from "styled-components";
import { rgba } from "polished";
import { padding } from "..";

const Nav = styled.nav`
  padding: 0.8rem 0;

  display: flex;
  justify-content: flex-end;
  align-items: center;
  background-color: #f9fafb;

  a {
    color: black;
    font-weight: 600;
    text-decoration: none;

    &:not(:last-child) {
      margin-right: 2em;
    }
  }

  .active {
    color: #a88687;
  }

  .dropdown {
    position: relative;
    display: inline-block;

    &:hover {
      ul {
        display: block;
      }
    }

    ul {
      display: none;
      background-color: white;
      position: absolute;
      top: 100%;
      list-style-type: none;
      padding: 0;
      box-shadow: 0px 1px 8px ${rgba("#000", 0.12)};
      padding: ${padding.m};

      li {
        padding: ${padding.sm} 0;
      }
    }
  }
`;

export default Nav;
