// TOASTER https://jossmac.github.io/react-toast-notifications/
import React, { FC, useState, useEffect } from "react";
import { useRouter } from "next/router";
import { useQuery, useMutation } from "@apollo/react-hooks";
import { useToasts } from "react-toast-notifications";
import {
  GET_SINGLE_PRODUCT,
  UPDATE_CHECKOUT,
  CREATE_CHECKOUT,
} from "../../src/graphql/schema.graphql";

import { titlelize } from "../../helpers";
import { Container, padding } from "../../styles";
import { IProductByHandle } from "./types";
import ProductGallery from "../../components/ProductGallery/ProductGallery";
import { MainContent, Aside, Wrap } from "./Product.styles";
import ProductOptions from "../../components/ProductGallery/components/ProductOptions";
import { formatCurrency } from "../../helpers/formatting";
import { useCart } from "../../src/context/cart";

enum ToasterContent {
  Error = "Er is iets fout gegaan. Probeer opnieuw",
  Success = "Product is succesvol toegevoegd",
}

const SingleProduct: FC = () => {
  const router = useRouter();
  const { pid } = router.query;
  const { addToast } = useToasts();
  const [variant, setVariant] = useState(null);
  const [checkoutId, setCheckoutId] = useState<string>();

  const { cartContent, setCartResponse } = useCart();

  const { data, loading }: IProductByHandle = useQuery(GET_SINGLE_PRODUCT, {
    variables: {
      handle: pid,
    },
  });

  const [createCheckout] = useMutation(CREATE_CHECKOUT, {
    variables: {
      input: {
        lineItems: [
          {
            quantity: 1,
            variantId: variant && variant.id,
          },
        ],
      },
    },
  });

  const [updateCart] = useMutation(UPDATE_CHECKOUT, {
    variables: {
      checkoutId: checkoutId,
      lineItems: [
        {
          quantity: 1,
          variantId: variant && variant.id,
        },
      ],
    },
  });

  const productTitle = titlelize(pid, true);

  useEffect(() => {
    if (!data) return;
    setVariant(data.productByHandle.variants.edges[0].node);
    setCheckoutId(localStorage.getItem("checkoutId"));
  }, [loading, data]);

  if (loading || !variant) {
    return null;
  }

  const product = data.productByHandle;
  const hasMultipleImages = product.images.edges.length > 1;
  const hasNoImage = !Boolean(product.images.edges.length);
  const hasProductOptions = Boolean(product.options);
  console.log(product.options);

  const handleVariantChange = (id: string) => {
    const { node: variant } = product.variants.edges.find(
      ({ node }) => node.id === id
    );

    setVariant(variant);
  };

  const handleDirectBuy = () => {
    createCheckout()
      .then((res) => res)
      .then(({ data }) => {
        if (!checkoutId) {
          const id = data.checkoutCreate.checkout.id;
          setCheckoutId(id);
          localStorage.setItem("checkoutId", id);
        }

        window.open(data.checkoutCreate.checkout.webUrl, "_blank");
      });
  };

  const handleUpdateCart = async () => {
    if (!checkoutId) {
      await createCheckout()
        .then((res) => res)
        .then(({ data }) => {
          const id = data.checkoutCreate.checkout.id;
          localStorage.setItem("checkoutId", id);
          setCheckoutId(id);
          setCartResponse(JSON.stringify(data.checkoutCreate.checkout));
        });
    } else {
      await updateCart()
        .then((res) => res)
        .then(({ data }) => {
          setCartResponse(JSON.stringify(data.checkoutLineItemsAdd.checkout));
        })
        .then(() =>
          addToast(ToasterContent.Success, {
            appearance: "success",
            autoDismiss: true,
          })
        )
        .catch((err) =>
          addToast(ToasterContent.Error, {
            appearance: "error",
            autoDismiss: true,
          })
        );
    }
  };

  console.log(hasProductOptions);

  return (
    <>
      <Container>
        <div style={{ padding: `${padding.xxl} 0 ${padding.xl}` }}></div>
      </Container>

      <Wrap>
        <Container style={{ display: "flex" }}>
          <MainContent>
            <ProductGallery
              hasMultipleImages={hasMultipleImages}
              hasNoImage={hasNoImage}
              images={product.images.edges}
            />
          </MainContent>

          <Aside>
            {variant.currentlyNotInStock && (
              <span className="note warning margin-bottom-1">
                Tijdelijk niet beschikbaar
              </span>
            )}

            <h1>{productTitle}</h1>

            <p style={{ marginBottom: "1em", marginTop: "0" }}>
              {product.description}
            </p>

            <h2 style={{ marginBottom: "1em", color: "rgb(151, 149, 149)" }}>
              {formatCurrency(variant.priceV2.amount)}
            </h2>

            {hasProductOptions && (
              <ProductOptions
                variants={product.variants}
                onVariantChange={handleVariantChange}
              />
            )}

            <button
              type="button"
              className="btn-primary"
              onClick={handleUpdateCart}
            >
              Add to cart
            </button>

            <button type="button" onClick={handleDirectBuy}>
              of koop direct
            </button>
          </Aside>
        </Container>
      </Wrap>
    </>
  );
};

export async function getStaticPaths() {
  return {
    paths: ["/product/[pid]"],
    fallback: true,
  };
}

export async function getStaticProps(props: { params: { pid: string } }) {
  return {
    props: {
      pid: props.params.pid,
    },
  };
}

export default SingleProduct;
