import styled from "styled-components";
import { rgba } from "polished";
import { breakpoint } from "../../styles";

export const Wrap = styled.div`
  box-shadow: 0px 2px 8px ${rgba("#000", 0.05)};
  border-radius: 4px;
  background-color: white;
  margin-top: 2em;

  > a {
    color: black;
    text-decoration: none;

    &:hover {
      h3 {
        color: blue;
      }
    }
  }
`;

//TODO Create different styling for seperation between checkout components
export const Row = styled.div`
  padding: 2em;

  @media screen and (max-width: ${breakpoint.md}) {
    align-items: flex-start;
  }

  &:not(:first-child) {
    border-top: 4px solid #f9fafb;
  }

  .thumbnail {
    position: relative;
    flex-shrink: 0;
    width: 100px;
    height: 100px;

    @media screen and (min-width: ${breakpoint.md}) {
      width: 125px;
      height: 125px;
    }

    > div {
      position: absolute;
      left: 0;
      top: 0;
      width: 100%;
      height: 100%;
      background-size: cover;
      background-position: center;
    }
  }

  .row-content {
    display: flex;
    flex-direction: column;

    @media screen and (min-width: ${breakpoint.md}) {
      flex-direction: row;
      flex-grow: 1;
    }
  }

  .text-content {
    flex-direction: column;
    padding: 0 2em;

    h3 {
      font-size: 1.4rem;
      margin: 0 0 0.5em;
    }

    p {
      opacity: 0.7;
      margin: 0;

      &:not(:last-child) {
        margin: 0 0 0.25em;
      }
    }
  }

  .actions {
    padding-left: 2em;
    margin-top: 1em;
    @media screen and (min-width: ${breakpoint.md}) {
      margin-left: auto;
      margin-top: 0;
    }
  }

  .remove-icon {
    width: 18px;
    height: 18px;
    margin: 0 0 0 0.5em;
  }

  .remove-button {
    display: flex;
    align-items: center;
  }
`;

export const PaymentActions = styled.div`
  margin: 3em 0 0;
  display: flex;
  justify-content: flex-end;
`;
