import styled from "styled-components";
import { rgba } from "polished";

export const Row = styled.div`
  border-radius: 4px;
  background-color: white;
  padding: 2em;
  box-shadow: 0px 2px 8px ${rgba("#000", 0.05)};

  &:first-child {
    margin-top: 6em;
  }

  &:not(:last-child) {
    margin: 0 0 1em;
  }

  .thumbnail {
    position: relative;
    width: 125px;
    height: 125px;

    > div {
      position: absolute;
      left: 0;
      top: 0;
      width: 100%;
      height: 100%;
      background-size: cover;
    }
  }
`;
