  const CartIcon = "/images/icons/cart.svg";

  return (
    <Wrap>
      <Container>
        <div className="content">
          <CartWrap>
            <Link href="/checkout/">
              <>
                <ReactSVG
                  src={CartIcon}
                  style={{
                    width: 24,
                    display: "inline-block",
                    cursor: "pointer",
                  }}
                />
                <span className="count">
                  <span>{cartQuantity}</span>
                </span>
              </>
            </Link>
          </CartWrap>
          <span className="price">{cartPrice}</span>
        </div>
      </Container>
    </Wrap>
  );
};

export default TopBar;
