import React, { FC } from "react";
import { useRouter } from "next/router";
import { useQuery } from "@apollo/react-hooks";

import { ISingleCollection } from "./types";

import { GET_SINGLE_COLLECTION } from "../../src/graphql/schema.graphql";
import { Container, padding } from "../../src/styles";
import { Row, Col } from "../index.styles";
import ProductCard from "../../src/components/shared/ProductCard/ProductCard";
import { ProductTypes } from "../../src/components/shared/ProductCard/types";
import { titlelize } from "../../src/helpers";

const SingleCategory: FC = () => {
  const router = useRouter();
  const { cid } = router.query;

  const { data, loading }: ISingleCollection = useQuery(GET_SINGLE_COLLECTION, {
    variables: {
      handle: cid,
    },
  });

  const categoryTitle = titlelize(cid, false);

  //TODO MAKE THIS A COMPONENT
  if (loading) {
    return (
      <Container>
        <div style={{ padding: `${padding.xxl} 0 ${padding.xl}` }}>
          <h1 className="section-title">
            <span>Alle {categoryTitle}</span>
          </h1>
        </div>
      </Container>
    );
  }

  const { edges } = data.collectionByHandle.products;

  return (
    <Container>
      <div style={{ padding: `${padding.xxl} 0 ${padding.xl}` }}>
        <h1 className="section-title">
          <span>Alle {categoryTitle}</span>
        </h1>
      </div>
      <Row>
        {edges.map(({ node }) => {
          const nodeImage = node.images.edges[0]?.node.originalSrc;
          return (
            <Col key={node.id}>
              <ProductCard
                type={ProductTypes.Product}
                title={titlelize(node.title, false, true)}
                handle={node.handle}
                imageUrl={nodeImage}
              />
            </Col>
          );
        })}
      </Row>
    </Container>
  );
};

export async function getStaticPaths() {
  return {
    paths: [
      "/categorie/verlichting",
      "/categorie/banden",
      "/categorie/marathonwagens",
    ],
    fallback: true,
  };
}

export async function getStaticProps(props: { params: { cid: string } }) {
  return {
    props: {
      cid: props.params.cid,
    },
  };
}

export default SingleCategory;
