import React from "react";
import { useQuery } from "@apollo/react-hooks";
import Link from "next/link";

import { ICollectionsResponse } from "./types";
import { GET_COLLECTIONS } from "../src/graphql/schema.graphql";
import { Container, padding } from "../src/styles";
import { Row, Col, Card } from "index.styles";

function Home() {
  const { data, loading } = useQuery<ICollectionsResponse>(GET_COLLECTIONS);
  const LongArrow = "/images/icons/2-SPAN.svg";

  if (loading) {
    return null;
  }

  return (
    <Container>
      <div style={{ margin: `${padding.l} 0` }}>
        <h1>Onze categorieÃ«n</h1>
      </div>
      <Row>
        {data.collections.edges.map(({ node: category }) => (
          <Col key={category.id}>
            <Card>
              <Link
                href="/categorie/[cid]"
                as={`/categorie/${category.handle}`}
              >
                <a>
                  <div className="image-wrap">
                    <div
                      className="image"
                      aria-label={category.title}
                      style={{
                        backgroundImage: `url(${category.image.originalSrc})`,
                      }}
                    ></div>
                  </div>
                  <div className="card-content">
                    {/* <h3 className="card-title">{category.title}</h3> */}
                    <p>
                      Ontdek alle {category.title} <ReactSVG src={longArrow} />
                    </p>
                  </div>
                </a>
              </Link>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default Home;
