import React, { FC } from "react";
import Link from "next/link";
import { ReactSVG } from "react-svg";

import { Container, Nav } from "../../../src/styles";

interface IHeaderProps {
  isLoadingNavItems: boolean;
  navItems: Array<any>;
}

const Header: FC<IHeaderProps> = ({ isLoadingNavItems, navItems }) => {
  const Logo = "/images/icons/2-SPAN.svg";
  console.log(navItems);

  return (
    <Nav>
      <Container className="flex flex-end items-center">
        <div style={{ marginRight: "auto" }}>
          <Link href="/">
            <div className="logo">
              <ReactSVG src={Logo} wrapper="div" style={{ width: "100%" }} />
            </div>
          </Link>
        </div>

        <Link href="/">Homepagina</Link>

        {!isLoadingNavItems &&
          navItems.collections.edges.map(({ node }) => (
            <Link href={node.handle}>${node.title}</Link>
          ))}
        {/* <NavLink to="/" exact>
          Homepagina
        </NavLink>
        {!loading &&
          collections.edges.map(({ node: collection }) => (
            <NavLink
              key={collection.handle}
              to={{
                pathname: `/categorie/${collection.handle}`,
                state: { title: collection.title },
              }}
            >
              {collection.title}
            </NavLink>
          ))} */}
      </Container>
    </Nav>
  );
};

export default Header;
