import React, { FC } from "react";
import { useRouter } from "next/router";
import { useQuery } from "@apollo/react-hooks";

import { ISingleCollection } from "./types";

import { GET_SINGLE_COLLECTION } from "../../src/graphql/schema.graphql";
import { Container, padding } from "../../src/styles";

const titlelize = (handle: any, capitalized = false, unCapitlized = false) => {
  let strippedTitle = handle.split("-").join(" ");

  if (capitalized) {
    strippedTitle.charAt(0).toUpperCase() + handle.slice(1);
  }

  if (unCapitlized) {
    strippedTitle.charAt(0).toLowerCase() + handle.slice(1);
  }

  return strippedTitle;
};

const SingleCategory: FC = (props) => {
  const router = useRouter();
  const { cid } = router.query;

  const { data, loading }: ISingleCollection = useQuery(GET_SINGLE_COLLECTION, {
    variables: {
      handle: cid,
    },
  });

  const categoryTitle = titlelize(cid, true);

  if (loading) {
    return (
      <Container>
        <div style={{ padding: `${padding.xxl} 0 ${padding.xl}` }}>
          <h1 className="section-title">
            <span>Alle {categoryTitle}</span>
          </h1>
        </div>
      </Container>
    );
  }

  const { edges } = data.collectionByHandle.products;

  return (
    <Container>
      <div style={{ padding: `${padding.xxl} 0 ${padding.xl}` }}>
        <h1 className="section-title">
          <span>Alle {categoryTitle}</span>
        </h1>
      </div>

      {edges.map(({ node }) => (
        <p key={node.title}>{node.title}</p>
      ))}
    </Container>
  );
};

export async function getStaticPaths() {
  return {
    paths: [
      "/categorie/verlichting",
      "/categorie/banden",
      "/categorie/marathonwagens",
    ],
    fallback: true,
  };
}

export async function getStaticProps(props: { params: { cid: string } }) {
  return {
    props: {
      cid: props.params.cid,
    },
  };
}

export default SingleCategory;
