import React from "react";
import NextApp from "next/app";
import Head from "next/head";
import { ToastProvider } from "react-toast-notifications";

import ApolloClient from "apollo-boost";
import { ApolloProvider, useQuery } from "@apollo/react-hooks";
import { InMemoryCache } from "apollo-cache-inmemory";

import { ThemeProvider } from "styled-components";

import MainLayout from "../layouts/main/MainLayout";
import { CartContextProvider } from "../src/context/cart";

const theme = {
  primary: "green",
};

export default class App extends NextApp {
  render() {
    const { Component, pageProps } = this.props;

    const client = new ApolloClient({
      uri: process.env.NEXT_PUBLIC_SHOPIFY_ACCESS_URL,
      cache: new InMemoryCache(),

      headers: {
        Accept: "application/json",
        "X-Shopify-Storefront-Access-Token":
          process.env.NEXT_PUBLIC_SHOPIFY_ACCESS_TOKEN,
      },

      onError: (err) => console.log(err),
    });

    return (
      <ApolloProvider client={client}>
        <Head>
          {/* Bij 2-Span hebben wij alles in huis voor uw aanspanning. Van accessoires tot rijtuigen. */}
          <title>2-Span | Alles voor uw aanspanning</title>
          <meta
            property="og:title"
            content="2-Span | Alles voor uw aanspanning"
            key="title"
          />
        </Head>
        <CartContextProvider>
          <ThemeProvider theme={theme}>
            <MainLayout>
              <ToastProvider>
                <Component {...pageProps} />
              </ToastProvider>
            </MainLayout>
          </ThemeProvider>
        </CartContextProvider>
      </ApolloProvider>
    );
  }
}
