import styled from "styled-components";
import breakpoints from "../../src/styles/misc/breakpoints";
import { rgba } from "polished";

export const MainImage = styled.div`
  width: 100%;
  padding: 0 0 60%;
  position: relative;

  div {
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-size: contain;
    background-position: center;
  }
`;

export const Wrap = styled.div`
  display: flex;
`;

export const MainContent = styled.div`
  @media screen and (min-width: ${breakpoints.md}) {
    width: 40%;
  }

  .thumbnail-wrap {
    display: flex;
    position: relative;
    margin: 16px -16px 0 -16px;
    padding: 0 8px;

    .thumbnail-image {
      margin: 0 8px;

      img {
        width: 100%;
      }
    }
  }
`;

export const Aside = styled.div`
  background-color: white;
  flex-grow: 1;
  position: relative;
  padding: 2em;
  box-shadow: 0px 4px 10px ${rgba("#000", 0.1)};

  @media screen and (min-width: ${breakpoints.md}) {
    margin-left: 3em;
  }

  &:after {
    content: "";
    position: absolute;
    left: 100%;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: white;
    display: block;
  }
`;
