import React from "react";
import { useQuery } from "@apollo/react-hooks";

import { GET_COLLECTIONS } from "../../src/graphql/schema.graphql";
import { Container } from "../../src/styles";
// import { Container } from "../src/styles";

type TSingleProduct = {
  handle: string;
  id: string;
};

type TImage = {
  id: string;
  altText: string;
  originalSrc: string;
};

type TSingleCollection = {
  node: {
    handle: string;
    id: string;
    image: TImage;
    products: TSingleProduct;
    title: string;
  };
};

interface ICollectionsResponse {
  collections: {
    edges: Array<TSingleCollection>;
  };
  loading: boolean;
}

function Category() {
  const { data, loading } = useQuery<ICollectionsResponse>(GET_COLLECTIONS);

  if (loading) {
    return <p>Loading</p>;
  }

  return (
    <Container>
      <div>
        {data.collections.edges.map(({ node: category }: TSingleCollection) => (
          <p>{category.title}</p>
        ))}
      </div>
    </Container>
  );
}

export default Category;
