import React, { FC } from "react";
import { useRouter } from "next/router";
import { useQuery } from "@apollo/react-hooks";
import { GET_SINGLE_PRODUCT } from "../../src/graphql/schema.graphql";

import { titlelize } from "../../src/helpers";
import { Container, padding } from "../../src/styles";
import { IProductByHandle } from "./types";
import ProductGallery from "./components/ProductGallery/ProductGallery";

const SingleProduct: FC = () => {
  const router = useRouter();
  const { pid } = router.query;

  const { data, loading }: IProductByHandle = useQuery(GET_SINGLE_PRODUCT, {
    variables: {
      handle: pid,
    },
  });

  const productTitle = titlelize(pid, true);

  if (loading) {
    return (
      <Container>
        <div style={{ padding: `${padding.xxl} 0 ${padding.xl}` }}>
          <h1 className="section-title">
            <span>{productTitle}</span>
          </h1>
        </div>
      </Container>
    );
  }

  const product = data.productByHandle;
  const hasMultipleImages = product.images.edges.length > 1;

  return (
    <Container>
      <div style={{ padding: `${padding.xxl} 0 ${padding.xl}` }}>
        <h1 className="section-title">
          <span>{productTitle}</span>
        </h1>
      </div>

      <ProductGallery
        hasMultipleImages={hasMultipleImages}
        images={product.images.edges}
      />
    </Container>
  );
};

export async function getStaticPaths() {
  return {
    paths: ["/product/[pid]"],
    fallback: true,
  };
}

export async function getStaticProps(props: { params: { pid: string } }) {
  return {
    props: {
      pid: props.params.pid,
    },
  };
}

export default SingleProduct;
