import React, { FC } from "react";
import { titlelize } from "categorie/helpers";

interface IProductCardProductContent {
  price: string | number;
  maxPrice?: string | number;
  title: string;
}

const ProductCardProductContent: FC<IProductCardProductContent> = ({
  price,
  maxPrice,
  title,
}) => {
  return (
    <div className="card-content">
      <h4>{titlelize(title)}</h4>
      <span className="card-price">
        {price} {maxPrice && `- ${maxPrice}`}
      </span>
      <div> Bekijk </div>
    </div>
  );
};

export default ProductCardProductContent;
