import React, { FC } from "react";
import { useRouter } from "next/router";
import { useLauseLazyQuery } from "@apollo/react-hooks";

import { ISingleCollection } from "./types";

import { GET_SINGLE_COLLECTION } from "../../src/graphql/schema.graphql";
import { Container } from "../../src/styles";

const SingleCategory: FC = (props) => {
  const router = useRouter();
  const { cid } = router.query;

  const { data, loading }: ISingleCollection = useLazyQuery(
    GET_SINGLE_COLLECTION,
    {
      variables: {
        handle: cid,
      },
    }
  );

  if (loading) {
    return <p>loading</p>;
  }

  const { edges } = data.collectionByHandle.products;

  return (
    <Container>
      <p>I'm category: {cid}</p>

      {edges.map(({ node }) => (
        <p key={node.title}>{node.title}</p>
      ))}
    </Container>
  );
};

export async function getStaticPaths() {
  return {
    paths: [
      "/categorie/verlichting",
      "/categorie/banden",
      "/categorie/marathonwagens",
    ],
    fallback: true,
  };
}

export async function getStaticProps(props: { params: { cid: string } }) {
  return {
    props: {
      cid: props.params.cid,
    },
  };
}

export default SingleCategory;
