import React, { FC } from "react";
import { useRouter } from "next/router";
import { useQuery } from "@apollo/react-hooks";

import MainLayout from "../../layouts/main";
import { GET_SINGLE_COLLECTION } from "../../src/graphql/schema.graphql";

type TSingleProduct = {
  node: {
    id: string;
    title: string;
    handle: string;
  };
};

type TSingleCollection = {
  description?: string;
  id: string;
  products: {
    edges: Array<TSingleProduct>;
  };
};

interface ISingleCollection {
  data: {
    collectionByHandle: TSingleCollection;
  };
  loading: boolean;
}

const SingleCategory: FC = (props) => {
  const router = useRouter();
  const { cid } = router.query;
  // console.log({ props });

  // console.log(router);

  const { data, loading, error }: ISingleCollection = useQuery(
    GET_SINGLE_COLLECTION,
    {
      variables: {
        handle: cid,
      },
    }
  );

  // const { edges } = !loading && data.collectionByHandle.products;
  // console.log({ data, error });

  if (!loading) {
    console.log(data);
  }
  // console.log(data);

  return (
    <MainLayout>
      <>
        <p>I'm category: {cid}</p>

        {/* {!loading &&
          edges.map(({ node }) => {
            return <p>{node.title}</p>;
          })} */}
      </>
    </MainLayout>
  );
};

export default SingleCategory;
