import styled from "styled-components";
import { rgba } from "polished";

export const Row = styled.div`
  border-radius: 4px;
  background-color: white;
  padding: 2em;
  box-shadow: 0px 2px 8px ${rgba("#000", 0.2)};

  .thumbnail {
    position: relative;
    width: 300px;
    width: 300px;
    align-self: flex-start;

    > div {
      position: absolute;
      left: 0;
      top: 0;
      width: 100%;
      height: 100%;
      background-size: cover;
    }
  }
`;
