import React from "react";
import Link from "next/link";
import Head from "next/head";

import ApolloClient from "apollo-boost";
import { ApolloProvider } from "@apollo/react-hooks";
import { InMemoryCache } from "apollo-cache-inmemory";

import { TopBar } from "../../components/shared";

import GlobalStyles from "./index.styles";

interface IMainLayout {
  children: React.ReactElement;
}

const client = new ApolloClient({
  uri: process.env.REACT_APP_SHOPIFY_URL,
  cache: new InMemoryCache(),

  headers: {
    Accept: "application/json",
    "X-Shopify-Storefront-Access-Token": process.env.REACT_APP_SHOPIFY,
  },

  onError: (err) => console.log(err),
});

function MainLayout({ children }: IMainLayout) {
  return (
    <>
      <Head>
        <meta charset="utf-8" />
        <link rel="icon" href="%PUBLIC_URL%/favicon.ico" />
        <meta name="viewport" content="width=device-width, initial-scale=1" />
        <meta name="theme-color" content="#000000" />
        <meta
          name="keywords"
          content="Aanspanning, Tweespan, 2-span, Paarden, Koetsen, Marathonwagen"
        />

        <link
          href="https://fonts.googleapis.com/css2?family=Nunito+Sans:wght@400;600;700&display=swap"
          rel="stylesheet"
        />

        <link rel="apple-touch-icon" href="%PUBLIC_URL%/logo192.png" />
        <link rel="manifest" href="%PUBLIC_URL%/manifest.json" />
      </Head>
      <GlobalStyles />
      <ApolloProvider client={client}>
        <TopBar />
        <Link href="/about">
          <a>About</a>
        </Link>

        {children}
      </ApolloProvider>
    </>
  );
}

export default MainLayout;
