import React, { FC } from "react";
import Link from "next/link";

import { Card } from "./index.styles";
import { ReactSVG } from "react-svg";

enum ProductTypes {
  Overview = "overview",
  Product = "product",
}

interface IProductCard {
  title?: string;
  handle?: string;
  imageUrl?: string;
  type?: ProductTypes;
}

interface IProductCardOverviewContent {
  title: string;
  icon?: string;
  button?: string;
}

const ProductCardOverviewContent = ({
  title,
  icon,
  button,
}: IProductCardOverviewContent) => (
  <div className="card-content">
    {icon && (
      <p className="margin-0">
        Ontdek alle {title}
        <ReactSVG src={icon} className="card-arrow" />
      </p>
    )}
  </div>
);

const ProductCard: FC<IProductCard> = ({ title, handle, imageUrl, type }) => {
  const LongArrow = "/images/icons/long-arrow.svg";

  return (
    <Card>
      <Link href="/categorie/[cid]" as={`/categorie/${handle}`}>
        <a>
          <div className="image-wrap">
            <div
              className="image"
              aria-label={title}
              style={{
                backgroundImage: `url(${imageUrl})`,
              }}
            ></div>
          </div>
          {type === ProductTypes.Overview && (
            <ProductCardOverviewContent title={title} icon={LongArrow} />
          )}
        </a>
      </Link>
    </Card>
  );
};

export default ProductCard;
