import React from "react";
import { useQuery } from "@apollo/react-hooks";
import Link from "next/link";

import { ICollectionsResponse } from "./types";
import { GET_COLLECTIONS } from "../src/graphql/schema.graphql";
import { Container, padding } from "../src/styles";
import { Row, Col, Card } from "index.styles";
import { ReactSVG } from "react-svg";

function Home() {
  const { data, loading } = useQuery<ICollectionsResponse>(GET_COLLECTIONS);
  const LongArrow = "/images/icons/long-arrow.svg";

  if (loading) {
    <Container>
      <div style={{ margin: `${padding.xxl} 0 ${padding.xl}` }}>
        <h1 className="section-title">
          <span>Onze categorieën</span>
        </h1>
      </div>
    </Container>;
  }

  return (
    <Container>
      <div style={{ margin: `${padding.xxl} 0 ${padding.xl}` }}>
        <h1 className="section-title">
          <span>Onze categorieën</span>
        </h1>
      </div>
      <Row>
        {data.collections.edges.map(({ node: category }) => (
          <Col key={category.id}>
            <Card>
              <Link
                href="/categorie/[cid]"
                as={`/categorie/${category.handle}`}
              >
                <a>
                  <div className="image-wrap">
                    <div
                      className="image"
                      aria-label={category.title}
                      style={{
                        backgroundImage: `url(${category.image.originalSrc})`,
                      }}
                    ></div>
                  </div>
                  <div className="card-content">
                    <p className="margin-0">
                      Ontdek alle {category.title}{" "}
                      <ReactSVG src={LongArrow} className="card-arrow" />
                    </p>
                  </div>
                </a>
              </Link>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default Home;
