import React, { FC } from "react";
import Link from "next/link";
import Router from "next/router";
import { ReactSVG } from "react-svg";

import { Container, Nav } from "../../../src/styles";
import { useQuery } from "@apollo/react-hooks";
import { GET_COLLECTIONS } from "../../../src/graphql/schema.graphql";

type SingleNavItem = {
  node: {
    title: string;
    handle: string;
  };
};

const Header: FC = () => {
  const Logo = "/images/icons/2-SPAN.svg";

  const { data: navItems, loading: isLoadingNavItems } = useQuery(
    GET_COLLECTIONS
  );

  function handleClick() {
    Router.push("/categorie/[slug]", `/categorie/${node.handle}`, {
      shallow: true,
    });
  }

  return (
    <Nav>
      <Container className="flex flex-end items-center">
        <div style={{ marginRight: "auto" }}>
          <Link href="/">
            <div className="logo">
              <ReactSVG src={Logo} wrapper="div" style={{ width: "100%" }} />
            </div>
          </Link>
        </div>

        <Link href="/" as="/">
          <a>Homepagina</a>
        </Link>

        <span className="dropdown">
          CategorieÃ«n
          <div className="dropdown-content">
            <ul>
              {!isLoadingNavItems &&
                navItems.collections.edges.map(({ node }: SingleNavItem) => (
                  <li key={node.handle}>
                    {/* <Link
                      href="/categorie/[slug]"
                      as={`/categorie/${node.handle}`}
                      // onClick={() => console.log("jo")}
                    >
                      <a>{node.title}</a>
                    </Link> */}
                    <p onClick={handleClick}></p>
                  </li>
                ))}
            </ul>
          </div>
        </span>

        <Link href="/categorie/sale">
          <a>Sale</a>
        </Link>
      </Container>
    </Nav>
  );
};

export default Header;
