import React from "react";
import { useQuery } from "@apollo/react-hooks";

import { GET_COLLECTIONS } from "../src/graphql/schema.graphql";

type TSingleProduct = {
  handle: string;
  id: string;
};

type TImage = {
  id: string;
  altText: string;
  originalSrc: string;
};

type TSingleCollection = {
  node: {
    handle: string;
    id: string;
    image: TImage;
    products: TSingleProduct;
  };
};

interface ICollectionsResponse {
  collections: {
    edges: Array<TSingleCollection>;
  };
  loading: boolean;
}

function Home() {
  const { data, loading } = useQuery<ICollectionsResponse>(GET_COLLECTIONS);

  if (loading) {
    return <p>Loading</p>;
  }

  console.log(data);

  return (
    <div className="container">
      {data.collections.edges.map(({ node: category }: TSingleCollection) => {
        console.llg(node);
      })}
    </div>
  );
}

export default Home;
