import React, { FC } from "react";
import { useRouter } from "next/router";
import { useQuery } from "@apollo/react-hooks";

import { GET_SINGLE_COLLECTION } from "../../src/graphql/schema.graphql";

type TSingleProduct = {
  node: {
    id: string;
    title: string;
    handle: string;
  };
};

type TSingleCollection = {
  description?: string;
  id: string;
  products: {
    edges: Array<TSingleProduct>;
  };
};

interface ISingleCollection {
  data: {
    collectionByHandle: TSingleCollection;
  };
  loading: boolean;
}

function SingleCategory() {
  const router = useRouter();
  const { cid } = router.query;

  const { data, loading }: ISingleCollection = useQuery(GET_SINGLE_COLLECTION, {
    variables: {
      handle: cid,
    },
  });

  if (loading) {
    return <p>loading</p>;
  }

  const { edges } = data.collectionByHandle.products;

  return (
    <>
      <p>I'm category: {cid}</p>

      {/* {edges.map(({ node }) => {
          return <p key={node.title}>{node.title}</p>} */}

      {edges.map(({ node }) => (
        <p key={node.title}>{node.title}</p>
      ))}
    </>
  );
}

// SingleCategory.getInitialProps({query}) {
//   console.log('SLUG', query.slug)
//   return {}
// }

export async function getStaticPaths() {
  return {
    paths: ["/categorie/marathon-wagens"],
    fallback: true,
  };
}

export async function getStaticProps({ query }: any) {
  return {
    query,
  };
}

export default SingleCategory;
