import React, { FC } from "react";
import { TVariant } from "product/types";

interface IProductOptions {
  variants: {
    edges: TVariant[];
  };
  onChange: (id: string) => void;
}

const ProductOptions: FC<IProductOptions> = ({ variants, onChange }: any) => {
  const handleSelectChange = ({ currentTarget }) => {
    console.log(currentTarget.value);
  };

  return (
    <div>
      <h4 style={{ marginBottom: "0.5em" }}>Beschikbare varianten:</h4>
      <select style={{ width: "300px" }} onChange={handleSelectChange}>
        {variants.edges.map(({ node }: TVariant) => (
          <option key={node.id}>{node.title}</option>
        ))}
      </select>
    </div>
  );
};

export default ProductOptions;
