import React, { FC, useState } from "react";
import Link from "next/link";
import { ReactSVG } from "react-svg";

import { Container, Nav, padding } from "../../../src/styles";

type SingleNavItem = {
  node: {
    title: string;
    handle: string;
  };
};

type NavItems = {
  collections: {
    edges: Array<SingleNavItem>;
  };
};

interface IHeaderProps {
  isLoadingNavItems: boolean;
  navItems: NavItems;
}

const Header: FC<IHeaderProps> = ({ isLoadingNavItems, navItems }) => {
  const Logo = "/images/icons/2-SPAN.svg";
  const [dropdownVisible, setDropdownVisible] = useState(false);

  return (
    <Nav>
      <Container className="flex flex-end items-center">
        <div style={{ marginRight: "auto" }}>
          <Link href="/">
            <div className="logo">
              <ReactSVG src={Logo} wrapper="div" style={{ width: "100%" }} />
            </div>
          </Link>
        </div>

        <Link href="/">Homepagina</Link>

        <span className="dropdown">
          CategorieÃ«n
          <div className="dropdown-content">
            <ul>
              {!isLoadingNavItems &&
                navItems.collections.edges.map(({ node }: SingleNavItem) => (
                  <li>
                    <Link href={`/categorie/${node.handle}`}>
                      <a>{node.title}</a>
                    </Link>
                  </li>
                ))}
            </ul>
          </div>
        </span>
      </Container>
    </Nav>
  );
};

export default Header;
