import React, { FC } from "react";
import Link from "next/link";

import { Card } from "./index.styles";
import { ReactSVG } from "react-svg";

interface IProductCard {
  title?: string;
  handle?: string;
  imageUrl?: string;
  type?: string;
}

const ProductCardOverviewContent = ({ title, icon }) => (
  <div className="card-content">
    <p className="margin-0">
      Ontdek alle {title}
      <ReactSVG src={icon} className="card-arrow" />
    </p>
  </div>
);

const ProductCard: FC<IProductCard> = ({ title, handle, imageUrl }) => {
  const LongArrow = "/images/icons/long-arrow.svg";

  return (
    <Card>
      <Link href="/categorie/[cid]" as={`/categorie/${handle}`}>
        <a>
          <div className="image-wrap">
            <div
              className="image"
              aria-label={title}
              style={{
                backgroundImage: `url(${imageUrl})`,
              }}
            ></div>
          </div>
          {type === "overview"}
        </a>
      </Link>
    </Card>
  );
};

export default ProductCard;
