import React, { FC } from "react";
import { Wrap, CartWrap } from "./TopBar.styles";
import { Container } from "../../styles";
import Link from "next/link";

import { ReactSVG } from "react-svg";
import { useCart } from "../../src/context/cart";

type TLineItem = {
  node: {
    quantity: number;
  };
};

type TCart = {
  quantity?: number;
  price?: number;
};

const TopBar: FC = () => {
  const { cartContent } = useCart();
  const parsedCart = Boolean(cartContent) && JSON.parse(cartContent);
  let cart: TCart = {
    quantity: 0,
    price: 0,
  };

  if (Boolean(parsedContent)) {
    cart = {
      quantity: parsedCart.lineItems.edges.reduce(
        (acc: number, obj: TLineItem) => acc + obj.node.quantity,
        0
      ),
      price: parsedCart.totalPriceV2,
    };
  }

  // const cartQuantity = parsedCart.lineItems.edges.reduce(
  //   (acc: number, obj: TLineItem) => acc + obj.node.quantity,
  //   0
  // );

  // const cartPrice = parsedCart.totalPriceV2;

  const CartIcon = "/images/icons/cart.svg";

  return (
    <Wrap>
      <Container>
        <div className="content">
          <CartWrap>
            <Link href="/checkout/">
              <>
                <ReactSVG
                  src={CartIcon}
                  style={{
                    width: 24,
                    display: "inline-block",
                    cursor: "pointer",
                  }}
                />
                <span className="count">
                  <span>{cart.quantity}</span>
                </span>
              </>
            </Link>
          </CartWrap>
          <span className="price">{cart.price}</span>
        </div>
      </Container>
    </Wrap>
  );
};

export default TopBar;
