import React, { FC } from "react";
import { useRouter } from "next/router";
import { useQuery } from "@apollo/react-hooks";

import { GET_SINGLE_COLLECTION } from "../../src/graphql/schema.graphql";

type TSingleProduct = {
  node: {
    id: string;
    title: string;
    handle: string;
  };
};

type TSingleCollection = {
  description?: string;
  id: string;
  products: {
    edges: Array<TSingleProduct>;
  };
};

interface ISingleCollection {
  data: {
    collectionByHandle: TSingleCollection;
  };
  loading: boolean;
}

function SingleCategory(props: any) {
  console.log(props);
  // const router = useRouter();
  // const { cid } = router.query;

  if (!props.cid) {
    return <p>loading</p>;
  }

  const { data, loading }: ISingleCollection = useQuery(GET_SINGLE_COLLECTION, {
    variables: {
      handle: props.cid,
    },
  });

  const { edges } = data.collectionByHandle.products;

  return (
    <>
      <p>I'm category: {props.cid}</p>

      {/* {edges.map(({ node }) => {
          return <p key={node.title}>{node.title}</p>} */}

      {edges.map(({ node }) => (
        <p key={node.title}>{node.title}</p>
      ))}
    </>
  );
}

// SingleCategory.getInitialProps({query}) {
//   console.log('SLUG', query.slug)
//   return {}
// }

export async function getStaticPaths() {
  console.log("static paths");

  return {
    paths: ["/categorie/banden"],
    fallback: true,
  };
}

export async function getStaticProps(props: { params: { cid: string } }) {
  console.log("static props");
  return {
    props: {
      cid: props.params.cid,
    },
  };
}

export default SingleCategory;
