export const titlelize = (
  handle: any,
  capitalized = false,
  unCapitlized = false
) => {
  let title = handle;

  if (capitalized) {
    title = title.charAt(0).toUpperCase() + handle.slice(1);
  }

  if (unCapitlized) {
    title = title.charAt(0).toLowerCase() + handle.slice(1);
  }

  const strippedTitle = title.split("-").join(" ");

  return strippedTitle;
};
