import React, { FC } from "react";

interface IProductQuantitySelect {
  onChange: (val: number) => void;
  maxAmount: number;
}

const ProductQuantitySelect: FC<IProductQuantitySelect> = ({
  onChange,
  maxAmount,
}) => {
  const handleChange = (event: { currentTarget: { value: string } }) => {
    const value = parseInt(event.currentTarget.value);
    console.log({ value });
    onChange(value);
  };

  return (
    <select onChange={handleChange} style={{ width: "75px" }}>
      {Array.from(Array(maxAmount)).map((_, index) => {
        return (
          <option key={index} value={index + 1}>
            {index + 1}
          </option>
        );
      })}
    </select>
  );
};

export default ProductQuantitySelect;
