import React, { FC } from "react";

interface IProductGallery {
  hasMultipleImages: boolean;
  hasNoImage: boolean;
  images: any[];
}

const ProductGallery: FC<IProductGallery> = ({
  hasMultipleImages,
  hasNoImage,
  images,
}) => {
  if (hasMultipleImages) {
    return <p>Multiple</p>;
  }

  if (hasNoImage) {
    return <p>no image</p>;
  }

  return (
    <>
      <p>Single or none</p>
    </>
  );
};

export default ProductGallery;
